package strings

import org.scalatest._

class SteadyGeneSpec extends FlatSpec with Matchers {

  val SampleGene4 = "GACC"
  val SampleGene8 = "GACCTTTT"
  val SampleGene82 = "GAAATAAA"
  val SampleGene16 = "CCCCCCTTTTTTTTTT"
  val SampleGene = ""
  val SampleGene500000 = generate(500000)

  def generate (n:Int) : String = {
    import SteadyGeneSolution._
    val wordBytes = new Array[Byte](n)
    for(i <- 0 to n - 1) {
      wordBytes(i) = elements(math.floor((MAX_ELEMENTS + 1) * math.random).toInt)
    }
    return new String(wordBytes)
  }


  val testContainer1 = Array(0,2,0,0)
  val testContainer2 = Array(0,2,1,0)
  val testContainer3 = Array(0,2,1,0)




  "Solution" should "should properly is contained" in {

    SteadyGeneSolution.getDistance(testContainer1, testContainer1) shouldBe 0
    SteadyGeneSolution.getDistance(testContainer1, testContainer2) shouldBe 0
    SteadyGeneSolution.getDistance(testContainer2, testContainer3) shouldBe 0
    SteadyGeneSolution.getDistance(testContainer3, testContainer2) shouldBe 0
    SteadyGeneSolution.getDistance(testContainer3, testContainer1) shouldBe 1
    SteadyGeneSolution.getDistance(testContainer2, testContainer1) shouldBe 1
  }

  "Solution" should "find the minumum regardless of the order" in {
    SteadyGeneSolution.practicalMinimum(SampleGene4) shouldBe 1
    SteadyGeneSolution.practicalMinimum(SampleGene8) shouldBe 2
    SteadyGeneSolution.practicalMinimum(SampleGene16) shouldBe 8
    SteadyGeneSolution.practicalMinimum(SampleGene) shouldBe 9138
    SteadyGeneSolution.practicalMinimum(SampleGene500000)
  }


  "Solution" should "considering order" in {
    SteadyGeneSolution.practicalMinimum( "CCCCCCTTTTTTTTTT") shouldBe 8
  }

  "Solution" should "considering in the middle" in {
    SteadyGeneSolution.practicalMinimum( "GAAATAAA") shouldBe 5
  }




}